   state = {count : 0};


componentDidMount(){

}
    render(){
        return (
            <div>
                <div>{this.state.count}</div>
                <div>{this.props.widgetName}</div>
                <button onClick = {() => this.updateCounter()}>vote</button>
            </div>
        )
    }


    updateCounter = () => {
        let updatedCounter = this.state.count;
        console.log(updatedCounter);
        this.setState({count:++updatedCounter});
    }

}
